pub enum SampleFormat {

    /// The value 0 corresponds to 0.
    I16,

    /// The value 0 corresponds to 32768.
    U16,

    /// The boundaries are (-1.0, 1.0).
    F32,
}

pub enum StreamData<'a> {
    Input {
        buffer: UnknownTypeInputBuffer,
    },
    Output {
        buffer: UnknownTypeOutputBuffer,
    },
}

pub enum UnknownTypeInputBuffer<'a> {

    /// Samples whose format is `u16`.
    U16(InputBuffer),

    /// Samples whose format is `i16`.
    I16(InputBuffer),

    /// Samples whose format is `f32`.
    F32(InputBuffer),
}

pub enum UnknownTypeOutputBuffer<'a> {

    /// Samples whose format is `u16`.
    U16(OutputBuffer),

    /// Samples whose format is `i16`.
    I16(OutputBuffer),

    /// Samples whose format is `f32`.
    F32(OutputBuffer),
}

pub enum FormatsEnumerationError {

    /// The device no longer exists. This can happen if the device is disconnected while the
    /// program is running.
    DeviceNotAvailable,
}

pub enum DefaultFormatError {

    /// The device no longer exists. This can happen if the device is disconnected while the
    /// program is running.
    DeviceNotAvailable,

    /// Returned if e.g. the default input format was requested on an output-only audio device.
    StreamTypeNotSupported,
}

pub enum CreationError {

    /// The device no longer exists. This can happen if the device is disconnected while the
    /// program is running.
    DeviceNotAvailable,

    /// The required format is not supported.
    FormatNotSupported,
}
